import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faGithub, faLinkedin } from '@fortawesome/free-brands-svg-icons';
import './Contact.css';

function Contact() {
  const [form, setForm] = useState({ name: '', email: '', message: '' });
  const [submitted, setSubmitted] = useState(false);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // submit to backend API (public endpoint)
    fetch('http://localhost:5000/api/contact', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(form),
    }).then(async (res) => {
      if (res.ok) {
        setSubmitted(true);
      } else {
        const json = await res.json().catch(() => ({}));
        alert('Failed to send message: ' + (json.error || res.statusText));
      }
    }).catch((err) => {
      console.error(err);
      alert('Network error while sending message');
    });
  };

  return (
    <section className="contact" id="contact" data-aos="fade-up">
      <h2 className="section-title">Contact Me</h2>
      <div className="contact__container">
        <form className="contact__form" onSubmit={handleSubmit}>
          <input
            type="text"
            name="name"
            placeholder="Your Name"
            value={form.name}
            onChange={handleChange}
            required
          />
          <input
            type="email"
            name="email"
            placeholder="Your Email"
            value={form.email}
            onChange={handleChange}
            required
          />
          <textarea
            name="message"
            placeholder="Your Message"
            value={form.message}
            onChange={handleChange}
            required
            rows={5}
          />
          <button type="submit" className="contact__submit">Send Message</button>
          {submitted && <div className="contact__thanks">Thank you! I'll get back to you soon.</div>}
        </form>
        <div className="contact__info">
          <p>Email: <a href="mailto:yuvanshankar@email.com">yuvansekar10@email.com</a></p>
          <div className="contact__socials">
            <a href="https://github.com/yuvanshankars" target="_blank" rel="noopener noreferrer">
              <FontAwesomeIcon icon={faGithub} />
            </a>
            <a href="https://www.linkedin.com/in/yuvanshankar-sekar-78aa622a7/" target="_blank" rel="noopener noreferrer">
              <FontAwesomeIcon icon={faLinkedin} />
            </a>
          </div>
        </div>
      </div>
    </section>
  );
}

export default Contact;
